// Parse Indeed jobs from HTML
const html = items[0].json.data || '';
const jobs = [];
const config = $node['Configure Search'].json;

// Extract job cards
const jobMatches = html.matchAll(/<div[^>]*class="[^"]*job_seen_beacon[^"]*"[^>]*data-jk="([^"]+)"[^>]*>([\s\S]*?)<\/div>/g);

for (const match of jobMatches) {
  const jobId = match[1];
  const jobHtml = match[2];

  const titleMatch = jobHtml.match(/<h2[^>]*class="jobTitle"[^>]*>.*?<span[^>]*title="([^"]+)"/);
  const companyMatch = jobHtml.match(/<span[^>]*class="companyName"[^>]*>([^<]+)<\/span>/);
  const locationMatch = jobHtml.match(/<div[^>]*class="companyLocation"[^>]*>([^<]+)<\/div>/);
  const salaryMatch = jobHtml.match(/<div[^>]*class="metadata salary-snippet-container"[^>]*>([^<]+)<\/div>/);

  if (titleMatch && companyMatch) {
    const title = titleMatch[1].trim();

    const isExcluded = config.excluded.some(word => 
      title.toLowerCase().includes(word.toLowerCase())
    );

    if (!isExcluded && jobs.length < 15) {
      jobs.push({
        jobId: `IN-${jobId}`,
        title: title,
        company: companyMatch[1].trim(),
        location: locationMatch ? locationMatch[1].trim() : config.location,
        url: `https://www.indeed.com/viewjob?jk=${jobId}`,
        source: 'Indeed',
        datePosted: new Date().toISOString().split('T')[0],
        dateFound: new Date().toISOString().split('T')[0],
        status: 'New',
        applied: 'No',
        salary: salaryMatch ? salaryMatch[1].trim() : 'Not specified',
        notes: ''
      });
    }
  }
}

return jobs.map(job => ({ json: job }));

// Parse Remotive API response
const response = items[0].json;
const jobs = [];
const config = $node['Configure Search'].json;

if (response.jobs && Array.isArray(response.jobs)) {
  for (const job of response.jobs.slice(0, 15)) {
    const title = job.title || '';

    const isExcluded = config.excluded.some(word => 
      title.toLowerCase().includes(word.toLowerCase())
    );

    if (!isExcluded) {
      jobs.push({
        jobId: `RM-${job.id}`,
        title: title,
        company: job.company_name || 'Unknown',
        location: 'Remote',
        url: job.url || '',
        source: 'Remotive',
        datePosted: job.publication_date ? job.publication_date.split('T')[0] : new Date().toISOString().split('T')[0],
        dateFound: new Date().toISOString().split('T')[0],
        status: 'New',
        applied: 'No',
        salary: job.salary || 'Not specified',
        notes: job.job_type || ''
      });
    }
  }
}

return jobs.map(job => ({ json: job }));

// Remove duplicate jobs based on jobId
const uniqueJobs = [];
const seenIds = new Set();

for (const item of items) {
  const jobId = item.json.jobId;
  if (!seenIds.has(jobId)) {
    seenIds.add(jobId);
    uniqueJobs.push(item);
  }
}

return uniqueJobs;

// Create email summary
const allJobs = items;
const newJobs = allJobs.filter(item => item.json.status === 'New');

// Group by source
const bySource = {};
for (const job of newJobs) {
  const source = job.json.source;
  if (!bySource[source]) bySource[source] = [];
  bySource[source].push(job.json);
}

// Create HTML email
let emailHtml = `
<div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto;">
  <h1 style="color: #2563eb;">ðŸŽ¯ ${newJobs.length} New Job Opportunities!</h1>
  <p style="font-size: 16px; color: #666;">Found on ${new Date().toLocaleDateString()}</p>
  <hr style="border: 1px solid #e5e7eb;">
`;

for (const [source, jobs] of Object.entries(bySource)) {
  emailHtml += `
  <h2 style="color: #1f2937; margin-top: 30px;">${source} (${jobs.length})</h2>
  <ul style="list-style: none; padding: 0;">
  `;

  for (const job of jobs.slice(0, 5)) {
    emailHtml += `
    <li style="background: #f9fafb; padding: 15px; margin: 10px 0; border-radius: 8px; border-left: 4px solid #2563eb;">
      <h3 style="margin: 0 0 5px 0; color: #1f2937;">${job.title}</h3>
      <p style="margin: 5px 0; color: #6b7280;"><strong>Company:</strong> ${job.company}</p>
      <p style="margin: 5px 0; color: #6b7280;"><strong>Location:</strong> ${job.location}</p>
      ${job.salary !== 'Not specified' ? `<p style="margin: 5px 0; color: #059669;"><strong>Salary:</strong> ${job.salary}</p>` : ''}
      <a href="${job.url}" style="display: inline-block; margin-top: 10px; padding: 8px 16px; background: #2563eb; color: white; text-decoration: none; border-radius: 4px;">Apply Now â†’</a>
    </li>
    `;
  }

  emailHtml += `</ul>`;
}

emailHtml += `
  <hr style="border: 1px solid #e5e7eb; margin-top: 30px;">
  <p style="color: #6b7280; font-size: 14px;">ðŸ“Š Check your <strong>Google Sheet</strong> for the complete list with all ${newJobs.length} jobs!</p>
  <p style="color: #9ca3af; font-size: 12px;">This is an automated message from your Job Tracker workflow.</p>
</div>
`;

return [{
  json: {
    totalJobs: newJobs.length,
    sources: Object.keys(bySource),
    emailBody: emailHtml,
    subject: `ðŸŽ¯ ${newJobs.length} New Jobs Found - ${new Date().toLocaleDateString()}`
  }
}];

